// Import the chalk library
import chalk from "chalk";

// Function to output a colored joke
function colorizeJoke(joke) {
  // Split the joke into words
  const words = joke.split(" ");

  // Define an array of chalk colors
  const colors = ["red", "green", "yellow", "blue", "magenta", "cyan", "white"];

  // Initialize an empty string to store the colored joke
  let coloredJoke = "";

  // Iterate through each word and apply a different color
  for (let i = 0; i < words.length; i++) {
    const word = words[i];
    const color = colors[i % colors.length]; // Use modulo to repeat colors if more words than colors
    coloredJoke += chalk[color](word) + " ";
  }

  // Remove the trailing space
  coloredJoke = coloredJoke.trim();

  console.log(coloredJoke);
}

// Example joke
const joke =
  "Why don't scientists trust atoms? Because they make up everything.";

// Output the colored joke
colorizeJoke(joke);
